import React, { useState, useContext, useEffect } from "react";
import ContactContext from "../../context/contact/contactContext";
const ContactForm = () => {
  const contactContext = useContext(ContactContext);
  const { addContact, current } = contactContext;

  useEffect(() => {
    if (current !== null) {
      setContact(current);
    }
  });

  const [contact, setContact] = useState({
    name: "",
    description: "",
    type: "Nature",
    points: "",
  });

  const { name, description, type, points } = contact;
  const onChange = (e) =>
    setContact({ ...contact, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
    addContact(contact);
    contactContext.addContact(contact);
    setContact({
      name: "",
      description: "",
      type: "Nature",
      points: "",
    });
  };

  return (
    <form onSubmit={onSubmit}>
      <h2 className="text-primary">Add Challenge</h2>
      <input
        type="text"
        placeholder="Name"
        name="name"
        value={name}
        onChange={onChange}
      />
      <input
        type="text"
        placeholder="Description"
        value={description}
        name="description"
        onChange={onChange}
      />
      <input
        type="text"
        placeholder="Points"
        value={points}
        name="points"
        onChange={onChange}
      />
      <h5>Challenge Type</h5>
      <input
        type="radio"
        name="type"
        value="Nature"
        checked={type === "Nature"}
        onChange={onChange}
      />{" "}
      Nature{" "}
      <input
        type="radio"
        name="type"
        value="Energy"
        checked={type === "Energy"}
        onChange={onChange}
      />{" "}
      Energy
      <div>
        <input
          type="submit"
          value="Add Challenge"
          className="btn btn-primary btn-block"
        />
      </div>
    </form>
  );
};

export default ContactForm;
